import{_ as o,c as a,o as i,ag as d}from"./chunks/framework.ko2zIC2c.js";const l="/assets/command_mode.DLmrHrH4.png",c="/assets/insert_mode.DBkOBswm.png",n="/assets/commandline_mode.D0Xf73Hu.png",b=JSON.parse('{"title":"Vi/Vim","description":"","frontmatter":{},"headers":[],"relativePath":"development/linux/software/vim/vim.md","filePath":"development/linux/software/vim/vim.md","lastUpdated":1744689004000}'),m={name:"development/linux/software/vim/vim.md"};function r(t,e,s,h,u,p){return i(),a("div",null,e[0]||(e[0]=[d('<h1 id="vivim" tabindex="-1">Vi/Vim <a class="header-anchor" href="#vivim" aria-label="Permalink to &quot;Vi/Vim&quot;">​</a></h1><ul><li><a href="#vivim">Vi/Vim</a><ul><li><a href="#三种模式">三种模式</a><ul><li><a href="#一般模式-command-mode">一般模式 (command mode)</a></li><li><a href="#编辑模式-insert-mode">编辑模式 (insert mode)</a></li><li><a href="#命令行模式-command-line-mode">命令行模式 (command-line mode)</a></li></ul></li><li><a href="#按键操作">按键操作</a><ul><li><a href="#一般模式按键操作">一般模式按键操作</a><ul><li><a href="#移动光标">移动光标</a></li><li><a href="#查找操作">查找操作</a></li></ul></li><li><a href="#编辑模式按键操作">编辑模式按键操作</a></li><li><a href="#命令行模式按键操作">命令行模式按键操作</a></li></ul></li></ul></li></ul><h2 id="san-zhong-mo-shi" tabindex="-1">三种模式 <a class="header-anchor" href="#san-zhong-mo-shi" aria-label="Permalink to &quot;三种模式&quot;">​</a></h2><ul><li>[x] <a href="#一般模式-command-mode">一般模式 (command mode)</a></li><li>[x] <a href="#编辑模式-insert-mode">编辑模式 (insert mode)</a></li><li>[x] <a href="#命令行模式-command-line-mode">命令行模式 (command-line mode)</a></li></ul><h3 id="yi-ban-mo-shi-command-mode" tabindex="-1">一般模式 (command mode) <a class="header-anchor" href="#yi-ban-mo-shi-command-mode" aria-label="Permalink to &quot;一般模式 (command mode)&quot;">​</a></h3><p>一般模式(command mode)，也叫命令模式。在一般模式下</p><ul><li>按 <code>i</code> 进入<a href="#编辑模式-insert-mode">编辑模式 (insert mode)</a></li><li>按 <code>:</code> 进入<a href="#命令行模式-command-line-mode">命令行模式 (command-line mode)</a></li></ul><p>用 vi/vim 打开一个文件之后，会默认进入一般模式。</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">vi</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> hello.cpp</span></span></code></pre></div><p><img src="'+l+'" alt="命令模式 (command mode)"></p><p>一般模式下，可以移动光标，删除字符或者删除整行，也可以复制、粘贴文本内容</p><ul><li>移动光标</li><li>删除字符</li><li>删除整行</li><li>复制文本</li><li>粘贴文本</li></ul><h3 id="bian-ji-mo-shi-insert-mode" tabindex="-1">编辑模式 (insert mode) <a class="header-anchor" href="#bian-ji-mo-shi-insert-mode" aria-label="Permalink to &quot;编辑模式 (insert mode)&quot;">​</a></h3><p>编辑模式(insert mode)，也叫插入模式，在这个模式下可以自由编辑文本内容。</p><p>在一般模式下，按 <code>i/I</code> <code>o/O</code> <code>a/A</code>(insert), <code>r/R</code>(replace) 任意一个可以进入编辑模式，编辑模式英文为 insert mode ，所以便于记忆，习惯用 <code>i</code> 进入编辑模式。其中 <code>r/R</code> 是进入编辑模式中的替换</p><p>在编辑模式下，按 <code>Esc</code> 退回<a href="#一般模式-command-mode">一般模式 (command mode)</a></p><p><img src="'+c+'" alt="编辑模式 (insert mode)"></p><h3 id="ming-ling-xing-mo-shi-command-line-mode" tabindex="-1">命令行模式 (command-line mode) <a class="header-anchor" href="#ming-ling-xing-mo-shi-command-line-mode" aria-label="Permalink to &quot;命令行模式 (command-line mode)&quot;">​</a></h3><p>命令行模式(command-line mode)，也叫末行模式。</p><p>在一般模式下，按 <code>:</code> 会进入命令行模式，可以在 <code>:</code> 之后输入需要进行的操作</p><p>在命令行模式下，删除 <code>:</code> 及其后字符，退回至<a href="#一般模式-command-mode">一般模式 (command mode)</a></p><p><img src="'+n+'" alt="命令行模式 (command-line mode)"></p><h2 id="an-jian-cao-zuo" tabindex="-1">按键操作 <a class="header-anchor" href="#an-jian-cao-zuo" aria-label="Permalink to &quot;按键操作&quot;">​</a></h2><h3 id="yi-ban-mo-shi-an-jian-cao-zuo" tabindex="-1">一般模式按键操作 <a class="header-anchor" href="#yi-ban-mo-shi-an-jian-cao-zuo" aria-label="Permalink to &quot;一般模式按键操作&quot;">​</a></h3><h4 id="yi-dong-guang-biao" tabindex="-1">移动光标 <a class="header-anchor" href="#yi-dong-guang-biao" aria-label="Permalink to &quot;移动光标&quot;">​</a></h4><ul><li><code>h</code> 或 ⬅️ : 左移， 先后按下 <code>3</code> 和 <code>h</code> 左移3个字符</li><li><code>j</code> 或 ➡️ : 下移， <code>5j</code> 左移5行</li><li><code>k</code> 或 ⬆️ : 上移， <code>6k</code> 左移6个字符</li><li><code>l</code> 或 ⬇️ : 右移， <code>7l</code> 右移7两行</li><li><code>0</code> : 移动到当前行的最前</li><li><code>$</code> : 移动到当前行的最后</li><li><code>n+G</code> : 先输入数字 <code>n</code> 并按下 <code>G</code>(大写)，移动到当前文件的第n行。<code>gg</code>=<code>1G</code>，移动到首行</li><li><code>n+Enter</code> : 先输入数字 <code>n</code> 并按下 <code>Enter</code> ，向下移动n行</li><li><code>ctrl+f</code> : 屏幕向下移动一页</li><li><code>ctrl+b</code> : 屏幕向上移动一页</li><li><code>ctrl+d</code> : 屏幕向下移动半页</li><li><code>ctrl+u</code> : 屏幕向上移动半页</li></ul><h4 id="cha-zhao-cao-zuo" tabindex="-1">查找操作 <a class="header-anchor" href="#cha-zhao-cao-zuo" aria-label="Permalink to &quot;查找操作&quot;">​</a></h4><ul><li><code>/word</code> : 查找光标之后的内容 <code>word</code> ，若要查找单词 <code>linux</code> ，输入 <code>/word</code> 即可</li><li><code>?word</code> : 查找光标之前的内容 <code>word</code></li></ul><p>查找后</p><ul><li><code>n</code> : 重复前一个查找操作，查找下一个内容 <code>/</code> 则是向下， <code>？</code> 表示向上，理解为next</li><li><code>N</code> : 重复查找操作，与 <code>n</code> 操作相反</li></ul><h3 id="bian-ji-mo-shi-an-jian-cao-zuo" tabindex="-1">编辑模式按键操作 <a class="header-anchor" href="#bian-ji-mo-shi-an-jian-cao-zuo" aria-label="Permalink to &quot;编辑模式按键操作&quot;">​</a></h3><h3 id="ming-ling-xing-mo-shi-an-jian-cao-zuo" tabindex="-1">命令行模式按键操作 <a class="header-anchor" href="#ming-ling-xing-mo-shi-an-jian-cao-zuo" aria-label="Permalink to &quot;命令行模式按键操作&quot;">​</a></h3>',32)]))}const g=o(m,[["render",r]]);export{b as __pageData,g as default};
