import{_ as t,c as p,o as a,ag as o}from"./chunks/framework.ko2zIC2c.js";const E="/assets/230715-%E6%AD%A6%E6%B1%89%E5%A4%A9%E9%99%85%E7%BA%BF%E6%99%9A%E9%9C%9E-%E6%9C%BA%E4%BD%8D.D7oFd2xZ.jpg",e="/assets/230715-%E9%A1%BA%E6%B0%91%E5%A4%A7%E5%8E%A6%E6%99%9A%E9%9C%9E_%E5%B3%B0%E5%80%BC.xp1M0AzZ.gif",i="/assets/230715-%E6%AD%A6%E6%B1%89%E5%A4%A9%E9%99%85%E7%BA%BF%E6%99%9A%E9%9C%9E-DSC02851-pro.jHQKew4t.jpg",r="/assets/230715-%E6%AD%A6%E6%B1%89%E5%A4%A9%E9%99%85%E7%BA%BF%E6%99%9A%E9%9C%9E-%E5%85%A8%E6%99%AF.Be8tht5a.jpg",A="/assets/230716-%E7%90%B4%E5%8F%B0%E6%99%9A%E9%9C%9E-00616.BoRAv0xQ.jpg",c="/assets/230716-%E7%90%B4%E5%8F%B0%E6%99%9A%E9%9C%9E.Br5REsFE.gif",n="/assets/230716-%E7%90%B4%E5%8F%B0-193212_0131.Cg_nUqIp.jpg",B="/assets/230716-%E7%90%B4%E5%8F%B0-194903_0143.Dje170ru.jpg",m="/assets/230716-%E7%90%B4%E5%8F%B0-194953_0144.C8fxGt4A.jpg",l="/assets/230716-%E6%B1%89%E5%8F%A3%E5%9F%8E%E5%B8%82%E5%86%85%E9%80%8F-200244.CtcEIIZ6.jpg",_="/assets/230716-%E9%95%BF%E6%B1%9F%E4%B8%A4%E5%B2%B8%E5%A4%9C%E6%99%AF-200100.DcowB01V.jpg",u="/assets/230722-%E4%BA%91%E9%B9%A4%E5%A4%A7%E5%8E%A6%E6%99%9A%E9%9C%9E.Ta6FeOzD.gif",h="/assets/230722-%E4%BA%91%E9%B9%A4%E5%A4%A7%E5%8E%A6%E6%99%9A%E9%9C%9EDJI-194915_0268_D.B3-AdJUr.jpg",g="/assets/230722-%E4%BA%91%E9%B9%A4%E5%A4%A7%E5%8E%A6%E6%99%9A%E9%9C%9EDJI-195055-HDR-PANORAMA.DM6GaW5M.jpg",d="/assets/230830-%E9%B9%A6%E9%B9%89%E6%B4%B2%E9%95%BF%E6%B1%9F%E5%A4%A7%E6%A1%A5.CdDm6QZJ.gif",F="/assets/230830-%E9%B9%A6%E9%B9%89%E6%B4%B2%E9%95%BF%E6%B1%9F%E5%A4%A7%E6%A1%A5-183130_0781.DrlkCry6.jpg",b="/assets/230830-%E9%B9%A6%E9%B9%89%E6%B4%B2%E9%95%BF%E6%B1%9F%E5%A4%A7%E6%A1%A5-183532_0784.DAjKf_8h.jpg",q="/assets/230830-%E9%B9%A6%E9%B9%89%E6%B4%B2%E9%95%BF%E6%B1%9F%E5%A4%A7%E6%A1%A5-183716_0785.CHct1snd.jpg",k="/assets/230830-%E9%B9%A6%E9%B9%89%E6%B4%B2%E9%95%BF%E6%B1%9F%E5%A4%A7%E6%A1%A5-184041_0795.DbpKCzZ9.jpg",C="/assets/230830-%E9%B9%A6%E9%B9%89%E6%B4%B2%E9%95%BF%E6%B1%9F%E5%A4%A7%E6%A1%A5-174716_0729_D.RBFuBpMk.jpg",D="/assets/230830-%E6%AD%A6%E6%B3%B0%E9%97%B8%E8%B6%B3%E7%90%83%E5%9C%BA-190752_0807.KIbwd2Sb.jpg",f="/assets/230831-%E9%B9%A6%E9%B9%89%E6%B4%B2%E5%A4%A7%E6%A1%A5%E6%82%AC%E6%9C%88.C0kdXW7K.jpg",j="/assets/%E8%8E%89%E6%99%AF-%E9%A2%84%E6%B5%8B-090204.Dq2Pa0Qe.png",I="/assets/230904-%E4%BA%8C%E4%B8%83%E9%95%BF%E6%B1%9F%E5%A4%A7%E6%A1%A5-175242_0896.Dgz6T7DA.jpg",S="/assets/230904-%E4%BA%8C%E4%B8%83%E9%95%BF%E6%B1%9F%E5%A4%A7%E6%A1%A5.DFdbwZct.gif",x="/assets/230904-%E4%BA%8C%E4%B8%83%E9%95%BF%E6%B1%9F%E5%A4%A7%E6%A1%A5-180233_0909.C8svhT5_.jpg",w="/assets/230904-%E4%BA%8C%E4%B8%83%E9%95%BF%E6%B1%9F%E5%A4%A7%E6%A1%A5-181655_0935.C4qQYA02.jpg",H=JSON.parse('{"title":"2023年武汉盛夏","description":"","frontmatter":{"title":"2023年武汉盛夏","subtitle":"2023年武汉晚霞合集","author":"HenryZhu","date":"2023-09-08T00:00:00.000Z","useHeaderImage":true,"headerImage":"/images/wuhan/230715-武汉天际线晚霞-DSC02851-pro.jpg","headerMask":"rgb(67,65,47, .2)","tags":["摄影"]},"headers":[],"relativePath":"diary/wuhan23_sunset/wuhan23_sunset.md","filePath":"diary/wuhan23_sunset/wuhan23_sunset.md","lastUpdated":1724588569000}'),O={name:"diary/wuhan23_sunset/wuhan23_sunset.md"};function P(T,s,y,z,Z,M){return a(),p("div",null,s[0]||(s[0]=[o('<h1 id="2023nian-wu-han-sheng-xia" tabindex="-1">2023年武汉盛夏 <a class="header-anchor" href="#2023nian-wu-han-sheng-xia" aria-label="Permalink to &quot;2023年武汉盛夏&quot;">​</a></h1><blockquote><p>date: 2023-09-08</p></blockquote><p>大抵是离开武汉的倒数第二个夏天</p><p>在各个机位之间奔波，见证了一次次盛大的晚霞，也遗憾地目睹经典机位被封和消失</p><p>致那些再也回不去的机位：顺民大厦、汇文新都</p><p>最后一个 500 米的夏天，也好好珍惜最后半年的 500 米高空</p><h2 id="0715-zui-hou-yi-cishun-min" tabindex="-1">🌇 07.15 最后一次，顺民 <a class="header-anchor" href="#0715-zui-hou-yi-cishun-min" aria-label="Permalink to &quot;🌇 07.15 最后一次，顺民&quot;">​</a></h2><p>顺民大厦可谓是武汉最经典的机位，没有之一，一直希望能遇上一次世纪晚霞，彻底毕业</p><p>去过好几次了，也被保安抓下来过，但是人多的时候，保安也无能为力，反正没人听他的话，但是没想到最后，武汉人还是失去了「它」</p><p>应该是在这次的过几天，因为有些人在「某音」传播，导致楼顶上锁了，这个视角就只能航拍或者到其他机位了</p><p><img src="'+E+'" alt=""></p><p>那天，太阳落下后，云开始变得越来越厚，甚至担心，太阳落下去之后能不能穿透云层，但是我仍然看到了云层中的一丝裂缝</p><p>当大家以为鸽景再一次🐦了大家的时候</p><p>突然，云层亮了起来，短短一分钟之内，染红了整个天空，在场的所有人都措手不及，几十秒内，所有无人机即刻升空（飞控干扰达到了极点）</p><p><img src="'+e+'" alt=""></p><p>这次的晚霞要说震撼其实也没有，并没有达到世纪晚霞的级别，但是当回看延时的时候，还是会被那一瞬间、短短一分钟内整个天空的变化所震撼，印象深刻，也许只有当时在场默默守候的人才能感受到那种震撼</p><p><img src="'+i+'" alt=""></p><blockquote><p>28mm F8.0 1/8s ISO200</p></blockquote><p>随后补了个全景接片</p><p><img src="'+r+'" alt=""></p><blockquote><p>24mm F2.8 1/15s ISO230 180º全景拼接</p></blockquote><h2 id="0716-zhou-mo-lian-shao" tabindex="-1">🌇 07.16 周末连烧 <a class="header-anchor" href="#0716-zhou-mo-lian-shao" aria-label="Permalink to &quot;🌇 07.16 周末连烧&quot;">​</a></h2><p>琴台新手村机位，这里可以看到月湖、琴台大剧院、音乐厅、新建的琴台美术馆、汉江、月湖桥、越秀财富中心</p><p>这次拍是 7.16 的，因为刚好是周日，连烧了两天，人还是很多的，因此去的比较早把机位占了，还碰到了一个玩胶片的妹子</p><p><img src="'+A+'" alt=""></p><blockquote><p>16mm F8.0 1/4s ISO100</p></blockquote><p>总结一下，就是大烧，像是要弥补前一天的遗憾一样，狠狠地烧了半个小时，只用带一个 16-28 就好，反正越广越震撼</p><p><img src="'+c+'" alt=""></p><p>然后就是全景拼接和蓝调了</p><p><img src="'+n+'" alt=""></p><blockquote><p>24mm F2.8 1/30s ISO100 超广角全景拼接</p></blockquote><p><img src="'+B+'" alt=""></p><blockquote><p>24mm F2.8 1/15s ISO420 超广角全景拼接</p></blockquote><p><img src="'+m+'" alt=""></p><blockquote><p>24mm F2.8 1/15s ISO460 180º全景拼接</p></blockquote><p>还有就是蓝调过后，避开丑出天际的灯光秀，拍下的汉口城市内透，不考虑灯光秀，非常非常喜欢武汉的城市内透，所以武汉市政府，拜托了，省点电费吧</p><p><img src="'+l+'" alt=""></p><blockquote><p>24mm F2.8 1.6s ISO100</p></blockquote><p><img src="'+_+'" alt=""></p><blockquote><p>24mm F2.8 1.6s ISO100</p></blockquote><h2 id="0722-shun-min-ping-ti" tabindex="-1">🌇 07.22 顺民平替 <a class="header-anchor" href="#0722-shun-min-ping-ti" aria-label="Permalink to &quot;🌇 07.22 顺民平替&quot;">​</a></h2><p>恰逢泽哥回武汉散步，正好碰上了晚霞预告，就带他一起到顺民大厦，然而此时已经确认了顺民楼顶锁门了，再无希望了。只得前移至其他高楼</p><p><img src="'+u+'" alt=""></p><p>比较一般，但是也记录了完整的过程</p><p>不得不说，长江给武汉的景色增添了太多的亮点 <img src="'+h+'" alt=""></p><p>一眼望去能看到杨泗港、鹦鹉洲、一桥、二桥...</p><p><img src="'+g+'" alt=""></p><h2 id="0830-xia-ji-de-wei-ba" tabindex="-1">🌇 08.30 夏季的尾巴 <a class="header-anchor" href="#0830-xia-ji-de-wei-ba" aria-label="Permalink to &quot;🌇 08.30 夏季的尾巴&quot;">​</a></h2><p>鹦鹉洲长江大桥应该是我最喜欢的武汉的桥了，那一抹红色横卧在整个长江是那么的醒目</p><p>汇文新都也不是说被封吧，就是有些不长眼的开发商把楼房建在了前面，导致视角被遮挡了，按照基建狂魔的速度，明夏的晚霞估计就会把鹦鹉洲大桥第一个桥墩给彻底挡住，所以去了好几次，幸好，遇上了，毕业了</p><p>8.30 同样的，大烧，持续了半个小时</p><p><img src="'+d+'" alt=""></p><p>夕阳落下，天空的云彻底散开，云开始慢慢被照亮 <img src="'+F+'" alt=""></p><blockquote><p>24mm F5.6 1/30s ISO100 180º全景拼接</p></blockquote><p>逐渐，天空渐渐被染红 <img src="'+b+'" alt=""></p><blockquote><p>24mm F5.6 1/30s ISO100 180º全景拼接</p></blockquote><p><img src="'+q+'" alt=""></p><blockquote><p>24mm F5.6 1/30s ISO100 超广角全景拼接</p></blockquote><p>最终达到这场晚霞的极盛时刻，整个天空被染红了，慢慢地退去这抹红色，一切回归平静，直到夜幕降临 <img src="'+k+'" alt=""></p><blockquote><p>24mm F5.6 1/30s ISO100 180º全景拼接</p></blockquote><p>顺手拍了一张鹦鹉洲大桥的证件照 <img src="'+C+'" alt=""></p><blockquote><p>70mm F2.8 1/800s ISO100</p></blockquote><p>还有彻底天黑后，武泰闸体育场旁足球场的俯视 <img src="'+D+'" alt=""></p><h2 id="0831-man-yue-shi-ke" tabindex="-1">🌉 08.31 满月时刻 <a class="header-anchor" href="#0831-man-yue-shi-ke" aria-label="Permalink to &quot;🌉 08.31 满月时刻&quot;">​</a></h2><p>这天之后的第二天，是满月最圆的时候，鹦鹉洲叠加上满月，真是绝配 <img src="'+f+'" alt=""></p><p>但是拍的不好，又又又过曝了，比黄鹤楼难拍是真的，在鹦鹉洲大桥月升的时候，已经是比较晚了，此时月亮已经很亮了，所以没有控制好曝光</p><h2 id="0902-tai-feng-lai-lin" tabindex="-1">🌇 09.02 台风来临 <a class="header-anchor" href="#0902-tai-feng-lai-lin" aria-label="Permalink to &quot;🌇 09.02 台风来临&quot;">​</a></h2><p>台风来了，🐦景给出了逆天的预测</p><p><img src="'+j+'" alt=""></p><p>然而结果大家也看到了</p><h2 id="0904-tai-feng-guo-hou-de-cai-hong" tabindex="-1">🌇 09.04 台风过后的彩虹 <a class="header-anchor" href="#0904-tai-feng-guo-hou-de-cai-hong" aria-label="Permalink to &quot;🌇 09.04 台风过后的彩虹&quot;">​</a></h2><p>台风带来的“世纪晚霞”后的第一天，天空终于变得通透了，继续桥梁收集计划 (二七长江大桥)</p><p>晚霞开始前，天空出现了彩虹，持续了很长时间</p><p><img src="'+I+'" alt=""></p><p>然后就是平平无奇的小烧</p><p><img src="'+S+'" alt=""></p><p><img src="'+x+'" alt=""></p><p><img src="'+w+'" alt=""></p>',78)]))}const J=t(O,[["render",P]]);export{H as __pageData,J as default};
