import{_ as e,c as r,o as t,ag as n}from"./chunks/framework.ko2zIC2c.js";const i="/assets/login.CJK57u8D.png",h="/assets/status_overview-without-password.CumbVGHh.png",p="/assets/system_admin.D-Iny51L.png",_=JSON.parse('{"title":"OpenWRT in Raspberry","description":"","frontmatter":{},"headers":[],"relativePath":"hardware/raspberrypi/OpenWRT.md","filePath":"hardware/raspberrypi/OpenWRT.md","lastUpdated":1724588569000}'),o={name:"hardware/raspberrypi/OpenWRT.md"};function s(l,a,c,u,d,b){return t(),r("div",null,a[0]||(a[0]=[n('<p>OpenWRT_981029</p><h1 id="openwrt-in-raspberry" tabindex="-1">OpenWRT in Raspberry <a class="header-anchor" href="#openwrt-in-raspberry" aria-label="Permalink to &quot;OpenWRT in Raspberry&quot;">​</a></h1><h2 id="an-zhuang-he-ji-ben-chu-shi-hua" tabindex="-1">安装和基本初始化 <a class="header-anchor" href="#an-zhuang-he-ji-ben-chu-shi-hua" aria-label="Permalink to &quot;安装和基本初始化&quot;">​</a></h2><h3 id="xia-zai-an-zhuang-bao" tabindex="-1">下载安装包 <a class="header-anchor" href="#xia-zai-an-zhuang-bao" aria-label="Permalink to &quot;下载安装包&quot;">​</a></h3><h3 id="shao-lu-an-zhuang-bao" tabindex="-1">烧录安装包 <a class="header-anchor" href="#shao-lu-an-zhuang-bao" aria-label="Permalink to &quot;烧录安装包&quot;">​</a></h3><h3 id="deng-lu" tabindex="-1">登陆 <a class="header-anchor" href="#deng-lu" aria-label="Permalink to &quot;登陆&quot;">​</a></h3><p>用网线将树莓派连接自己的电脑，然后接上电源，稍等一会儿后访问 <a href="http://192.168.1.1" target="_blank" rel="noreferrer">192.168.1.1</a></p><p><img src="'+i+'" alt="login"></p><p>首次访问默认没有密码的，直接点击登陆，会进入状态界面 <img src="'+h+'" alt="首次登陆的状态概况"></p><p>首次登陆会提示要设置密码，根据指引跳转到<strong>密码设置页面</strong>(「系统」-「管理权」) <img src="'+p+'" alt="">()</p><h3 id="she-zhi-wei-pang-lu-you" tabindex="-1">设置为旁路由 <a class="header-anchor" href="#she-zhi-wei-pang-lu-you" aria-label="Permalink to &quot;设置为旁路由&quot;">​</a></h3><p>在「网络」—「接口」中，就只需要设置lan口，wan口不需要设置。</p><p><img src="https://pic3.zhimg.com/80/v2-54203e51353e8a3ea2693a5fad11fa0a_1440w.jpg" alt="基础的旁路由接线方式"></p><p>是不是有点挑战常识？主路由的LAN口居然接着旁路由的LAN口？没错，我没画错！</p><p>主路由可以是普通的无线路由器。所有联网设备都需要连接主路由。</p><p>网络数据在其中会怎样流动呢？</p><p><img src="https://pic1.zhimg.com/80/v2-e4e629ebc48d2bacc94f96882e768be8_1440w.jpg" alt="访问国内网站时的数据流动"></p><p><img src="https://pic2.zhimg.com/80/v2-d9a220afb0372e44a646cc93aebdda45_1440w.webp" alt="访问国外网站时的数据流动"></p><p>假设主路由的IP地址为192.168.1.1，请根据实际情况进行变通哦！</p><p>将旁路由的LAN连接到电脑的网口。没有电脑？旁路由不支持无线？试试这样？使用额外的无线路由器连接到旁路由，再使用手机等无线设备连接无线路由器，进而设置旁路由</p><h2 id="cha-jian" tabindex="-1">插件 <a class="header-anchor" href="#cha-jian" aria-label="Permalink to &quot;插件&quot;">​</a></h2><h2 id="can-kao" tabindex="-1">参考 <a class="header-anchor" href="#can-kao" aria-label="Permalink to &quot;参考&quot;">​</a></h2><ul><li><a href="https://zhuanlan.zhihu.com/p/112484256" target="_blank" rel="noreferrer">OpenWrt中，旁路由的设置与使用 - 知乎</a></li><li><a href="https://www.zhihu.com/question/562549702/answer/2749918777" target="_blank" rel="noreferrer">openwrt 作为旁路由如何连接？- 知乎</a></li></ul><p><a href="https://zhuanlan.zhihu.com/p/112484256" target="_blank" rel="noreferrer">OpenWrt中，旁路由的设置与使用 - 知乎</a></p>',24)]))}const g=e(o,[["render",s]]);export{_ as __pageData,g as default};
