import{_ as e,c as a,o as l,ag as r}from"./chunks/framework.ko2zIC2c.js";const y=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"hardware/raspberrypi/icm20602.md","filePath":"hardware/raspberrypi/icm20602.md","lastUpdated":1724588569000}'),d={name:"hardware/raspberrypi/icm20602.md"};function n(i,t,s,c,x,o){return l(),a("div",null,t[0]||(t[0]=[r('<h2 id="jie-shao" tabindex="-1">介绍 <a class="header-anchor" href="#jie-shao" aria-label="Permalink to &quot;介绍&quot;">​</a></h2><p>ICM-20602 是一个6轴运动跟踪设备，包括「<strong>3轴陀螺仪</strong>」和「<strong>3轴加速度计</strong>」，在一个小的 3 毫米 x 3 毫米 x 0.75 毫米（16 针 LGA）封装。它还具有1 KB FIFO，可以降低串行总线接口上的流量，并且通过允许系统处理器突发读取传感器数据，然后进入低功耗模式来降低功耗。ICM-20602，凭借其6轴集成，使制造商能够消除昂贵而复杂的选择、资格和系统离散设备的水平集成，保证消费者的最佳运动性能。</p><p>陀螺仪的可编程全尺度范围为±250 dps、±500 dps、±1000 dps和±2000 dps。加速度计具有用户可编程的加速度计全尺度范围为±2g、±4g、±8g和±16g。两个传感器的出厂校准初始灵敏度降低生产线校准要求。</p><p>其他行业领先的功能包括片上16位ADC、可编程数字滤波器、嵌入式温度传感器和可编程中断。该设备具有I2C和SPI串行接口，VDD 工作范围为 1.71V 至 3.45V，以及单独的数字IO电源，VDDIO 从 1.71V 到 3.45V。</p><h2 id="yu-shu-mei-pai-lian-jie" tabindex="-1">与树莓派链接 <a class="header-anchor" href="#yu-shu-mei-pai-lian-jie" aria-label="Permalink to &quot;与树莓派链接&quot;">​</a></h2><h3 id="jie-xian" tabindex="-1">接线 <a class="header-anchor" href="#jie-xian" aria-label="Permalink to &quot;接线&quot;">​</a></h3><table tabindex="0"><thead><tr><th style="text-align:center;">ICM-20602</th><th style="text-align:center;">树莓派</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:center;">VDD</td><td style="text-align:center;">17</td><td style="text-align:left;">3.3v</td></tr><tr><td style="text-align:center;">GND</td><td style="text-align:center;">25</td><td style="text-align:left;">地</td></tr><tr><td style="text-align:center;">SPC</td><td style="text-align:center;">23</td><td style="text-align:left;">SPI 时钟线</td></tr><tr><td style="text-align:center;">SDI</td><td style="text-align:center;">19</td><td style="text-align:left;">SPI 数据 MOSI</td></tr><tr><td style="text-align:center;">SDO</td><td style="text-align:center;">21</td><td style="text-align:left;">SPI 数据 MISO</td></tr><tr><td style="text-align:center;">CS</td><td style="text-align:center;">24/26</td><td style="text-align:left;">片选，二选一即可</td></tr><tr><td style="text-align:center;">INT</td><td style="text-align:center;">x</td><td style="text-align:left;">不接</td></tr><tr><td style="text-align:center;">FYS</td><td style="text-align:center;">39</td><td style="text-align:left;">同步数字输入（可选） 未使用接GND</td></tr></tbody></table>',7)]))}const h=e(d,[["render",n]]);export{y as __pageData,h as default};
