import{_ as e,o as r,c as a,a as n}from"./app.e2a6bb87.js";const i={},h=n('<h1 id="nlp-\u5B66\u4E60\u65E5\u5FD7" tabindex="-1"><a class="header-anchor" href="#nlp-\u5B66\u4E60\u65E5\u5FD7" aria-hidden="true">#</a> NLP \u5B66\u4E60\u65E5\u5FD7</h1><h2 id="nlp-\u57FA\u7840\u4EFB\u52A1" tabindex="-1"><a class="header-anchor" href="#nlp-\u57FA\u7840\u4EFB\u52A1" aria-hidden="true">#</a> NLP \u57FA\u7840\u4EFB\u52A1</h2><p>NLP\u7684\u56DB\u5927\u4EFB\u52A1<sup><a href="#ref-1">[1]</a></sup>\u5982\u4E0B\uFF1A</p><ul><li>\u5E8F\u5217\u6807\u6CE8\u4EFB\u52A1</li><li>\u5206\u7C7B\u4EFB\u52A1</li><li>\u53E5\u5B50\u5173\u7CFB\u5224\u65AD</li><li>\u751F\u6210\u5F0F\u4EFB\u52A1</li></ul><h3 id="\u5E8F\u5217\u6807\u6CE8\u4EFB\u52A1" tabindex="-1"><a class="header-anchor" href="#\u5E8F\u5217\u6807\u6CE8\u4EFB\u52A1" aria-hidden="true">#</a> \u5E8F\u5217\u6807\u6CE8\u4EFB\u52A1</h3><p>\u5E8F\u5217\u6807\u6CE8 (Sqeuence Labeling) \u4EFB\u52A1\u662F\u6307\uFF0C\u7ED9\u5B9A\u4E00\u4E2A\u5E8F\u5217\uFF0C\u4E3A\u5E8F\u5217\u4E2D\u7684\u6BCF\u4E2A\u5143\u7D20\u90FD\u6253\u4E0A\u4E00\u4E2A\u6807\u7B7E\u3002</p><blockquote><p>\u4E00\u822C\u6765\u8BF4\uFF0C\u4E00\u4E2A\u5E8F\u5217\u662F\u4E00\u4E2A\u53E5\u5B50</p></blockquote><p>\u5E8F\u5217\u6807\u6CE8\u4EFB\u52A1\u7684<strong>\u8F93\u5165\u662F\u4E00\u4E2A\u5E8F\u5217</strong>\uFF0C<strong>\u8F93\u51FA\u662F\u4E00\u4E2A\u6807\u7B7E\u5E8F\u5217</strong>\uFF0C<strong>\u6807\u7B7E\u5E8F\u5217\u7684\u957F\u5EA6\u4E0E\u8F93\u5165\u5E8F\u5217\u7684\u957F\u5EA6\u76F8\u540C</strong>\uFF0C\u6807\u7B7E\u5E8F\u5217\u4E2D\u7684\u6BCF\u4E2A\u5143\u7D20\u90FD\u5BF9\u5E94\u7740\u8F93\u5165\u5E8F\u5217\u4E2D\u7684\u4E00\u4E2A\u5143\u7D20\u3002</p><h2 id="rnn\u7684\u53D1\u5C55\u5386\u53F2" tabindex="-1"><a class="header-anchor" href="#rnn\u7684\u53D1\u5C55\u5386\u53F2" aria-hidden="true">#</a> RNN\u7684\u53D1\u5C55\u5386\u53F2</h2><p>1986\u5E74\uFF0CElman\u7B49\u4EBA\u63D0\u51FA\u4E86\u7528\u4E8E\u5904\u7406\u5E8F\u5217\u6570\u636E\u7684\u5FAA\u73AF\u795E\u7ECF\u7F51\u7EDC\u3002\u5982\u540C\u5377\u79EF\u795E\u7ECF\u7F51\u7EDC\u662F\u4E13\u95E8\u7528\u4E8E\u5904\u7406\u4E8C\u7EF4\u6570\u636E\u4FE1\u606F\uFF08\u5982\u56FE\u50CF\uFF09\u7684\u795E\u7ECF\u7F51\u7EDC\uFF0C\u5FAA\u73AF\u795E\u7ECF\u7F51\u7EDC\u662F\u4E13\u7528\u4E8E\u5904\u7406\u5E8F\u5217\u4FE1\u606F\u7684\u795E\u7ECF\u7F51\u7EDC\u3002\u5FAA\u73AF\u7F51\u7EDC\u53EF\u4EE5\u6269\u5C55\u5230\u66F4\u957F\u7684\u5E8F\u5217\uFF0C\u5927\u591A\u6570\u5FAA\u73AF\u795E\u7ECF\u7F51\u7EDC\u53EF\u4EE5\u5904\u7406\u53EF\u53D8\u957F\u5EA6\u7684\u5E8F\u5217\uFF0C\u5FAA\u73AF\u795E\u7ECF\u7F51\u7EDC\u7684\u8BDE\u751F\u89E3\u51B3\u4E86\u4F20\u7EDF\u795E\u7ECF\u7F51\u7EDC\u5728\u5904\u7406\u5E8F\u5217\u4FE1\u606F\u65B9\u9762\u7684\u5C40\u9650\u6027\u3002</p><p>1997\u5E74\uFF0CHochreiter\u548CSchmidhuber\u63D0\u51FA\u4E86\u957F\u77ED\u65F6\u8BB0\u5FC6\u5355\u5143(Long Short-Term Memory, LSTM) \u7528\u4E8E\u89E3\u51B3\u6807\u51C6\u5FAA\u73AF\u795E\u7ECF\u7F51\u7EDC\u65F6\u95F4\u7EF4\u5EA6\u7684\u68AF\u5EA6\u6D88\u5931\u95EE\u9898(vanishing gradient problem)\u3002\u6807\u51C6\u7684\u5FAA\u73AF\u795E\u7ECF\u7F51\u7EDC\u7ED3\u6784\u5B58\u50A8\u7684\u4E0A\u4E0B\u6587\u4FE1\u606F\u7684\u8303\u56F4\u6709\u9650\uFF0C\u9650\u5236\u4E86RNN\u7684\u5E94\u7528\u3002LSTM\u578BRNN\u7528LSTM\u5355\u5143\u66FF\u6362\u6807\u51C6\u7ED3\u6784\u4E2D\u7684\u795E\u7ECF\u5143\u8282\u70B9\uFF0CLSTM\u5355\u5143\u4F7F\u7528\u8F93\u5165\u95E8\u3001\u8F93\u51FA\u95E8\u548C\u9057\u5FD8\u95E8\u63A7\u5236\u5E8F\u5217\u4FE1\u606F\u7684\u4F20\u8F93\uFF0C\u4ECE\u800C\u5B9E\u73B0\u8F83\u5927\u8303\u56F4\u7684\u4E0A\u4E0B\u6587\u4FE1\u606F\u7684\u4FDD\u5B58\u4E0E\u4F20\u8F93\u3002</p><p>1998\u5E74\uFF0CWilliams\u548CZipser\u63D0\u51FA\u540D\u4E3A\u201C\u968F\u65F6\u95F4\u53CD\u5411\u4F20\u64AD(Backpropagation Through Time, BPTT)\u201D\u7684\u5FAA\u73AF\u795E\u7ECF\u7F51\u7EDC\u8BAD\u7EC3\u7B97\u6CD5\u3002BPTT\u7B97\u6CD5\u7684\u672C\u8D28\u662F\u6309\u7167\u65F6\u95F4\u5E8F\u5217\u5C06\u5FAA\u73AF\u795E\u7ECF\u7F51\u7EDC\u5C55\u5F00\uFF0C\u5C55\u5F00\u540E\u7684\u7F51\u7EDC\u5305\u542BN(\u65F6\u95F4\u6B65\u957F)\u4E2A\u9690\u542B\u5355\u5143\u548C\u4E00\u4E2A\u8F93\u51FA\u5355\u5143\uFF0C\u7136\u540E\u91C7\u7528\u53CD\u5411\u8BEF\u5DEE\u4F20\u64AD\u65B9\u5F0F\u5BF9\u795E\u7ECF\u7F51\u7EDC\u7684\u8FDE\u63A5\u6743\u503C\u8FDB\u884C\u66F4\u65B0\u3002</p><p>2001\u5E74\uFF0CGers\u548CSchmidhuber\u63D0\u51FA\u4E86\u5177\u6709\u91CD\u5927\u610F\u4E49\u7684LSTM\u578BRNN\u4F18\u5316\u6A21\u578B\uFF0C\u5728\u4F20\u7EDF\u7684LSTM\u5355\u5143\u4E2D\u52A0\u5165\u4E86\u7AA5\u89C6\u5B54\u8FDE\u63A5(peephole connections)\u3002\u5177\u6709\u7AA5\u89C6\u5B54\u8FDE\u63A5\u7684LSTM\u578BRNN\u6A21\u578B\u662F\u5FAA\u73AF\u795E\u7ECF\u7F51\u7EDC\u6700\u6D41\u884C\u7684\u6A21\u578B\u4E4B\u4E00\uFF0C\u7AA5\u89C6\u5B54\u8FDE\u63A5\u8FDB\u4E00\u6B65\u63D0\u9AD8\u4E86LSTM\u5355\u5143\u5BF9\u5177\u6709\u957F\u65F6\u95F4\u95F4\u9694\u76F8\u5173\u6027\u7279\u70B9\u7684\u5E8F\u5217\u4FE1\u606F\u7684\u5904\u7406\u80FD\u529B\u30022005\u5E74\uFF0CGraves\u6210\u529F\u5C06LSTM\u578BRNN\u5E94\u7528\u4E8E\u8BED\u97F3\u5904\u7406\uFF1B2007\u5E74\uFF0CHochreiter\u5C06LSTM\u578BRNN\u5E94\u7528\u4E8E\u751F\u7269\u4FE1\u606F\u5B66\u7814\u7A76\u3002</p><p><sup><a href="#ref-2">[2]</a></sup></p><h2 id="\u53C2\u8003\u548C\u6765\u6E90" tabindex="-1"><a class="header-anchor" href="#\u53C2\u8003\u548C\u6765\u6E90" aria-hidden="true">#</a> \u53C2\u8003\u548C\u6765\u6E90</h2><ul><li><p><span id="ref-1"></span> [1]<a href="https://zhuanlan.zhihu.com/p/109122090" target="_blank" rel="noopener noreferrer">NLP\u7684\u4EFB\u52A1 - Microstrong\u7684\u6587\u7AE0 - \u77E5\u4E4E</a></p></li><li><p><span id="ref-2"></span> [2]<a href="https://zhuanlan.zhihu.com/p/149869659" target="_blank" rel="noopener noreferrer">\u5FAA\u73AF\u795E\u7ECF\u7F51\u7EDC(RNN)\u77E5\u8BC6\u5165\u95E8 - ARGO\u521B\u65B0\u5B9E\u9A8C\u5BA4\u7684\u6587\u7AE0 - \u77E5\u4E4E</a></p></li><li><p><a href="https://github.com/PKU-TANGENT/nlp-tutorial" target="_blank" rel="noopener noreferrer">PKU-TANGENT nlp-tutorial</a>: \u672C\u6559\u7A0B\u4F9B\u65B0\u52A0\u5165 TANGENT \u5B9E\u9A8C\u5BA4\u7684\u540C\u5B66\u5165\u95E8 NLP \u4F7F\u7528</p></li></ul>',16),t=[h];function l(o,p){return r(),a("div",null,t)}var c=e(i,[["render",l],["__file","nlp.html.vue"]]);export{c as default};
